# 计算持仓相关数据
positions = {
    '002352.SZ': 200,
    '002594.SZ': 2600,
    '002714.SZ': 6300,
    '600276.SH': 600
}

prices = {
    '002352.SZ': 39.76,
    '002594.SZ': 92.32,
    '002714.SZ': 38.36,
    '600276.SH': 49.35
}

# 从历史交易总结获取止损价和目标价（近似值）
stop_loss = {
    '002352.SZ': 34.43,
    '002594.SZ': 84.59,
    '002714.SZ': 35.33,
    '600276.SH': 44.18
}

price_target = {
    '002352.SZ': 46.31,
    '002594.SZ': 117.5,
    '002714.SZ': 53.5,
    '600276.SH': 55.0
}

# 计算预期收益率
expected_return = {}
for code in positions.keys():
    if code in price_target and code in prices:
        exp_ret = (price_target[code] - prices[code]) / prices[code] * 100
        expected_return[code] = exp_ret

print("持仓股预期收益率:")
for code, ret in expected_return.items():
    print(f"{code}: {ret:.1f}%")

# 计算距离止损空间
stop_loss_space = {}
for code in positions.keys():
    if code in stop_loss and code in prices:
        space = (prices[code] - stop_loss[code]) / prices[code] * 100
        stop_loss_space[code] = space

print("\n距离止损空间:")
for code, space in stop_loss_space.items():
    print(f"{code}: {space:.1f}% (现价{prices[code]} > 止损{stop_loss[code]})")

# 总资产计算
cash = 79119.0
total_position_value = sum([positions[code] * prices[code] for code in positions])
total_assets = total_position_value + cash
print(f"\n总资产: {total_assets:.2f}元")
print(f"股票仓位: {total_position_value/total_assets*100:.1f}%")
print(f"现金比例: {cash/total_assets*100:.1f}%")
